import {Redirect} from 'react-router-dom';
import {useAuth} from 'hooks/use-auth';



const HomePage = () => {
    const {isAuth, email} = useAuth();
    return isAuth ? (
        <form ref={(el) => this.myForm = el}>
        <input placeholder="Имя" onChange={(e) => this.setState({firstname: e.target.value})}/>
        <input placeholder="Фамилия" onChange={(e) => this.setState({lastname: e.target.value})}/>
        <input placeholder="Мобильный телефон" onChange={(e) => this.setState({mobile: e.target.value})}/>
        <input placeholder="Почта" onChange={(e) => this.setState({mail: e.target.value})}/>
        <button type="button" onClick={() => {
            this.myForm.reset()
            this.userAdd = {
                firstname: this.state.firstname,
                lastname: this.state.lastname,
                mobile: this.state.mobile,
                mail: this.state.mail,
            }
            if (this.props.user)
                this.userAdd.id = this.props.user.id;
            this.props.onAdd(this.userAdd)
        }
        }>Добавить</button>
    </form>
    ) : (
        <Redirect to="/login"/>
    )
}

export default HomePage;
